pragma solidity 0.8.6;

import "../brokers/Broker_GMX_SHORT.sol";
import "../interfaces/IContractsStorage.sol";
import "../interfaces/IMarginAccount.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "../interfaces/IPositionRouter.sol";
import "hardhat/console.sol";

contract BrokerCreator_GMX_SHORT {
    IContractsStorage public contractsStorage;
    IPositionRouter public positionRouter;
    IERC721 public adminToken;
    IERC721 public tradeToken;
    address public router;
    address public vault;
    string name = "GMX_SHORT";
    address[] public arrTradingPair;

    constructor(
        IContractsStorage _contractsStorage,
        IERC721 _adminToken,
        IERC721 _tradeToken,
        IPositionRouter _positionRouter,
        address _router,
        address _vault,
        address[] memory _arrTradingPair
    ) {
        contractsStorage = _contractsStorage;
        adminToken = _adminToken;
        tradeToken = _tradeToken;
        positionRouter = _positionRouter;
        router = _router;
        vault = _vault;
        arrTradingPair = _arrTradingPair;
    } 

    function createBroker(uint256 id) external {
        require(msg.sender == adminToken.ownerOf(id));
        address marginAccountAddress = contractsStorage.marginAccounts(id);
        require(
            IMarginAccount(marginAccountAddress).brokers(name) == address(0),
            "MarginAccount for this address already exist"
        );
        Broker_GMX_SHORT newBroker = new Broker_GMX_SHORT(
            name,
            id,
            marginAccountAddress,
            address(contractsStorage.usdc()),
            router,
            vault,
            contractsStorage,
            positionRouter,
            adminToken,
            tradeToken,
            arrTradingPair
        );
        IMarginAccount(marginAccountAddress).addBroker(name, address(newBroker));
    }
}

