contracts/brokers/Broker_GMX_SHORT.sol.content:
// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "../interfaces/IPositionRouter.sol";
import "../interfaces/IContractsStorage.sol";
import "../interfaces/ILendAndBorrowManager.sol";
import "../vaultion_managers/IVaultionManager.sol";
import "../interfaces/IRouter.sol";
import "../interfaces/IVault.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "hardhat/console.sol";

contract Broker_GMX_SHORT {
    uint256 public constant GMX_DECIMALS = 1e30;
    IContractsStorage public contractsStorage;
    IPositionRouter public positionRouter;
    IERC721 public tradeToken;
    IERC721 public adminToken;
    string public brokerName;
    uint256 public key;
    address public marginAccount;
    address public usdc;
    address public router;
    address public vault;
    bool public isApproved = false;
    address[] public arrTradingPair;

    constructor(
        string memory _brokerName,
        uint256 _key,
        address _marginAccount,
        address _usdc,
        address _router,
        address _vault,
        IContractsStorage _contractsStorage,
        IPositionRouter _positionRouter,
        IERC721 _adminToken,
        IERC721 _tradeToken,
        address[] memory _arrTradingPair
    ) {
        brokerName = _brokerName;
        key = _key;
        marginAccount = _marginAccount;
        usdc = _usdc;
        router = _router;
        vault = _vault;
        contractsStorage = _contractsStorage;
        positionRouter = _positionRouter;
        adminToken = _adminToken;
        tradeToken = _tradeToken;
        arrTradingPair = _arrTradingPair;
    }

    function liquidatePositions() external payable {
        require(msg.sender == contractsStorage.riskManager(), "you not a risk manager");
        uint256 executionFee = checkMsgValue();
        for (uint256 i; i < arrTradingPair.length; i++) {
            (uint256 size, uint256 closeValue, uint256 maxPrice) = positionInformation(arrTradingPair[i]);
            if (size != 0) {
                positionRouter.createDecreasePosition{value: executionFee}(
                    getGmxPath(),
                    arrTradingPair[i],
                    closeValue,
                    size,
                    false,
                    address(this),
                    maxPrice*101/100,
                    0,
                    executionFee,
                    false,
                    address(0)
                );
            }
        }
    }

    function checkMsgValue() internal returns (uint256 executionFee) {
        executionFee = positionRouter.minExecutionFee();
        uint256 correctMsgValue;
        for (uint256 i; i < arrTradingPair.length; i++) {
            (uint256 size,,) = positionInformation(arrTradingPair[i]);
            if (size != 0) {
                correctMsgValue += executionFee;
            }
        }
        require(msg.value >= correctMsgValue, "invalid msg.value");
    }

    function positionInformation(address indexToken) public view returns (uint256 size, uint256 closeValue, uint256 maxPrice) {
        (uint256 positionSize, uint256 collateral, , , , , , ) = IVault(vault).getPosition(address(this), usdc, indexToken, false);
        size = positionSize;
        if (size != 0) {
            (bool isProfit, uint256 delta) = IVault(vault).getPositionDelta(address(this), usdc, indexToken, false);
            closeValue = (isProfit ? collateral : delta);
            maxPrice = IVault(vault).getMinPrice(indexToken);
        }

    }

    function createIncreasePosition(
        address _indexToken,
        uint256 _amountIn,
        uint256 _minOut,
        uint256 _acceptablePrice,
        uint256 _executionFee,
        bytes32 _referralCode
    ) external payable {
        require(msg.sender == tradeToken.ownerOf(key) || msg.sender == adminToken.ownerOf(key), "LendAndBorrowManager: you are not the owner");
        uint256 usdcBalance = IVaultionManager(contractsStorage.vaultionManagers("USDC")).valueByAddress(address(this));
        uint256 borrowAmount;
        if (usdcBalance !=0) {
            if (_amountIn > usdcBalance) {
                borrowAmount = _amountIn - usdcBalance; 
            } else {
                borrowAmount = 0; 
            }
        } else {
            borrowAmount = _amountIn;
        }
        ILendAndBorrowManager(contractsStorage.lendAndBorrowManager()).sendByBroker(key, borrowAmount, msg.sender);
        uint256 _sizeDelta = _amountIn*(GMX_DECIMALS/1e6);
        positionRouter.createIncreasePosition{value: msg.value}(
            getGmxPath(),
            _indexToken,
            _amountIn,
            _minOut,
            _sizeDelta,
            false,
            _acceptablePrice,
            _executionFee,
            _referralCode,
            address(0)
        );
    }

    function createDecreasePosition(
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        uint256 _acceptablePrice,
        uint256 _minOut,
        uint256 _executionFee
    ) external payable {
        require(msg.sender == tradeToken.ownerOf(key) || msg.sender == adminToken.ownerOf(key), "LendAndBorrowManager: you are not the owner");
        positionRouter.createDecreasePosition{value: msg.value}(
            getGmxPath(),
            _indexToken,
            _collateralDelta,
            _sizeDelta,
            false,
            address(this),
            _acceptablePrice,
            _minOut,
            _executionFee,
            false,
            address(0)
        );
    }

    function RLcreateIncreasePosition(
        uint256 id,
        address[] memory _path,
        address _indexToken,
        uint256 _amountIn,
        uint256 _minOut,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        uint256 _executionFee,
        bytes32 _referralCode
    ) external payable {
        require(msg.sender == contractsStorage.reserveLiquidators(brokerName));
        positionRouter.createIncreasePosition{value: msg.value}(
            _path,
            _indexToken,
            _amountIn,
            _minOut,
            _sizeDelta,
            _isLong,
            _acceptablePrice,
            _executionFee,
            _referralCode,
            address(0)
        );
    }

    function RLcreateDecreasePosition(
        uint256 id,
        address[] memory _path,
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        uint256 _minOut,
        uint256 _executionFee,
        bool _withdrawETH
    ) external payable {
        require(msg.sender == contractsStorage.reserveLiquidators(brokerName));
        positionRouter.createDecreasePosition{value: msg.value}(
            _path,
            _indexToken,
            _collateralDelta,
            _sizeDelta,
            _isLong,
            address(this),
            _acceptablePrice,
            _minOut,
            _executionFee,
            _withdrawETH,
            address(0)
        );
    }

    function provideERC20(uint256 amount, string memory name) public {
        require(msg.sender == marginAccount);
        IVaultionManager vaultionManager = IVaultionManager(contractsStorage.vaultionManagers(name));
        IERC20(vaultionManager.token()).transferFrom(msg.sender, address(this), amount);
    }

    function withdrawERC20(uint256 amount, string memory name) public {
        require(msg.sender == contractsStorage.riskManager());
        IVaultionManager vaultionManager = IVaultionManager(contractsStorage.vaultionManagers(name));
        IERC20(vaultionManager.token()).transfer(contractsStorage.riskManager(), amount);
    }

    function approveAll(uint256 value) public {
        IRouter(router).approvePlugin(address(positionRouter));
        IERC20(usdc).approve(address(positionRouter), value);
        IERC20(usdc).approve(router, value);
        IERC20(usdc).approve(contractsStorage.riskManager(), value);
        isApproved = true;
    }


    function getGmxPath() internal view returns(address[] memory gmxPath) {
        gmxPath = new address[](1);
        gmxPath[0] = usdc;
    }
}

