// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IRoleManager.sol";
import "./interfaces/ILendAndBorrowManager.sol";
import "hardhat/console.sol";

contract ContractsStorage is Ownable {
    mapping(string => address) public brokerCreators;
    mapping(uint256 => address) public marginAccounts;
    mapping(string => address) public vaultionManagers;
    string[] public vaultionManagersArr;
    mapping(string => address) public reserveLiquidators;
    mapping(string => address) public triggerManagers;
    IERC721 public tradeToken;
    IERC721 public adminToken;
    address public riskManager;
    IRoleManager public roleManager;
    ILendAndBorrowManager public lendAndBorrowManager;
    IERC20 public usdc;

    constructor(
        IERC721 _adminToken,
        IERC721 _tradeToken,
        IRoleManager _roleManager,
        ILendAndBorrowManager _lendAndBorrowManager,
        IERC20 _usdc
    ) {
        adminToken = _adminToken;
        tradeToken = _tradeToken;
        roleManager = _roleManager;
        lendAndBorrowManager = _lendAndBorrowManager;
        usdc = _usdc;
    }

    function getProductArr() external view returns (string[] memory) {
        return vaultionManagersArr;
    }

    function setVaultionManager(string memory name, address vaultionManager) external onlyOwner {
        roleManager.checkCONTRACT_MANAGER(msg.sender);
        vaultionManagers[name] = vaultionManager;
    }

    function addVaultionManagersArr(string memory name) external onlyOwner {
        vaultionManagersArr.push(name);
    }

    function changeVaultionManagersArr(string[] memory nameArr) external onlyOwner {
        vaultionManagersArr = nameArr;
    }

    function setBrokerCreator(string memory name, address brokerCreator) external onlyOwner {
        roleManager.checkCONTRACT_MANAGER(msg.sender);
        brokerCreators[name] = brokerCreator;
    }

    function setMarginAccount(uint256 id, address marginAccount) external {
        roleManager.checkMARGIN_ACCOUNT_MANAGER_ROLE(msg.sender);
        marginAccounts[id] = marginAccount;
    }

    function setReserveLiquidators(string memory name, address reserveLiquidator) external onlyOwner {
        roleManager.checkCONTRACT_MANAGER(msg.sender);
        reserveLiquidators[name] = reserveLiquidator;
    }

    function changeRiskManager(address value) external onlyOwner {
        roleManager.checkCONTRACT_MANAGER(msg.sender);
        riskManager = value;
    }
}

