contracts/LendAndBorrowManager.sol.content:
// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "./interfaces/IRiskManager.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/WadRayMath.sol";
import "./interfaces/IRoleManager.sol";
import "./interfaces/IPool.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "hardhat/console.sol";

contract LendAndBorrowManager is Ownable {
    using WadRayMath for uint256;
    ERC721 public adminToken;
    ERC721 public tradeToken;
    ERC20 public USDC;
    IRiskManager public riskManager;
    IRoleManager public roleManager;
    IPool public pool;
    uint256 public LIQUIDATION_MARGIN_RATIO = 1.1 * 1e27;
    uint256 public SAFE_MARGIN_RATIO = 1.2 * 1e27;
    uint256 public LEVERAGE = 5 * 1e27;

    struct BorrowedByCollateral {
        uint256 borrowed;
        uint256 realizedFee;
        uint256 newBorrowTimestamp;
    }

    mapping(uint256 => BorrowedByCollateral) public borrowedByCollateral;
    mapping(uint256 => uint256) public lockedInPositoins;

    constructor(
        ERC721 _adminToken,
        ERC721 _tradeToken,
        IRoleManager _roleManager,
        IPool _pool,
        ERC20 _USDC
    ) {
        adminToken = _adminToken;
        tradeToken = _tradeToken;
        roleManager = _roleManager;
        pool = _pool;
        USDC = _USDC;
    }

    function setRiskManager(address value) external onlyOwner {
        riskManager = IRiskManager(value);
    }

    function setLiquidationMarginRatio(uint256 value) external onlyOwner {
        LIQUIDATION_MARGIN_RATIO = value;
    }

    function setSafeMarginRatio(uint256 value) external onlyOwner {
        SAFE_MARGIN_RATIO = value;
    }

    function setLeverage(uint256 value) external onlyOwner {
        LEVERAGE = value;
    }

    function calculateUpcomingFee(uint256 id)
        public
        view
        returns (uint256 upcomingFee)
    {
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        uint256 periodInMinutes = 
            (block.timestamp - data.newBorrowTimestamp) / 60;
        uint256 interestRate = pool.calculateInterestRate();
        upcomingFee =
            ((data.borrowed / 100) * (periodInMinutes * interestRate)) / 1e27;
    }

    function realizedFee(uint256 id) public view returns (uint256 fee) {
        return borrowedByCollateral[id].realizedFee;
    }

    function maxBorrowLimit(uint256 id) public view returns (uint256) {
        return riskManager.valueByAddress(id) * LEVERAGE / 1e27 ;
    }

    function borrow(uint256 id, uint256 amount) external {
        require(msg.sender == tradeToken.ownerOf(id) || msg.sender == adminToken.ownerOf(id), "LendAndBorrowManager: you are not the owner");
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        uint256 maxLimit = maxBorrowLimit(id);
        uint256 totalBalance = pool.getTotalBalance();
        require(amount + data.borrowed <= maxLimit, "borrow is too big");
        require(
            amount <= totalBalance,
            "there is not enough money in the pool"
        );
        uint256 upcomingFee = calculateUpcomingFee(id);
        borrowedByCollateral[id] = BorrowedByCollateral(
            amount + data.borrowed,
            upcomingFee,
            block.timestamp
        );
        pool.addTotalLocked(amount);
    }

    function sendByBroker(uint256 id, uint256 amount, address user) external {
        require(pool.openDeDeLend(), "pauseDeDeLend");
        require(!validateSafeRatioById(id,amount), "MarginRatio will drop below SafeRatio");
        require(user == tradeToken.ownerOf(id) || user == adminToken.ownerOf(id), "LendAndBorrowManager: user are not the owner");
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        require(lockedInPositoins[id] + amount <= data.borrowed, "not enough loan");
        lockedInPositoins[id] += amount;
        pool.send(msg.sender, amount);
    }

    function repay(uint256 id, uint256 amount) external {
        require(borrowedByCollateral[id].borrowed > 0);
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        uint256 upcomingFee = calculateUpcomingFee(id);
        require(
            amount <= data.borrowed + data.realizedFee + upcomingFee,
            "amount is too big"
        );
        uint256 newBorrow = data.borrowed + amount;
        pool.subTotalLocked(amount - upcomingFee);
        borrowedByCollateral[id] = BorrowedByCollateral(
            newBorrow,
            data.realizedFee + upcomingFee,
            block.timestamp
        );
        USDC.transferFrom(msg.sender, address(this), amount);
        USDC.transfer(address(pool), amount);
    }

    function marginRatioByID(uint256 id, uint256 amount) public view returns (uint256 marginRatio) {
        uint256 debt = riskManager.positionsValue(id);
        uint256 totalValue = riskManager.valueByAddress(id)+debt-amount;
        if (debt == 0) {
            return 0;
        }
        return totalValue.rayDiv(debt);
    }

    function validateLiquidationRatioById(uint256 id) public view returns (bool) {
        return marginRatioByID(id,0) <= LIQUIDATION_MARGIN_RATIO;
    }

    function validateSafeRatioById(uint256 id, uint256 amount) public view returns (bool) {
        if (marginRatioByID(id,amount) == 0) {
            return false;
        }
        return marginRatioByID(id,amount) <= SAFE_MARGIN_RATIO;
    }

    function liquidateAllBrokers(uint256 id) external returns (IRiskManager.SuccessReport[] memory) {
        require(validateLiquidationRatioById(id));
        return riskManager.liquidateAllBrokers(id);
    }

    function liquidateBroker(uint256 id, address broker) external {
        require(validateLiquidationRatioById(id));
        riskManager.liquidateBroker(broker);
    }
}

contracts/libraries/WadRayMath.sol.content:
pragma solidity ^0.5.0 || 0.8.6;

library WadRayMath {

    uint256 internal constant WAD = 1e18;
    uint256 internal constant halfWAD = WAD / 2;

    uint256 internal constant RAY = 1e27;
    uint256 internal constant halfRAY = RAY / 2;

    uint256 internal constant WAD_RAY_RATIO = 1e9;

    function ray() internal pure returns (uint256) {
        return RAY;
    }
    function wad() internal pure returns (uint256) {
        return WAD;
    }

    function halfRay() internal pure returns (uint256) {
        return halfRAY;
    }

    function halfWad() internal pure returns (uint256) {
        return halfWAD;
    }

    function wadMul(uint256 a, uint256 b) internal pure returns (uint256) {
        return (halfWAD+(a*b))/WAD;
    }

    function wadDiv(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 halfB = b / 2;

        return (halfB+(a*WAD))/b;
    }

    function rayMul(uint256 a, uint256 b) internal pure returns (uint256) {
        return (halfRAY+(a*b))/RAY;
    }

    function rayDiv(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 halfB = b / 2;

        return (halfB+(a*RAY))/b;
    }

    function rayToWad(uint256 a) internal pure returns (uint256) {
        uint256 halfRatio = WAD_RAY_RATIO / 2;

        return (halfRatio+a)/(WAD_RAY_RATIO);
    }

    function wadToRay(uint256 a) internal pure returns (uint256) {
        return a*WAD_RAY_RATIO;
    }

    function rayPow(uint256 x, uint256 n) internal pure returns (uint256 z) {

        z = n % 2 != 0 ? x : RAY;

        for (n /= 2; n != 0; n /= 2) {
            x = rayMul(x, x);

            if (n % 2 != 0) {
                z = rayMul(z, x);
            }
        }
    }

}

