// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "./interfaces/IRoleManager.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/ILendAndBorrowManager.sol";
import "./interfaces/IContractsStorage.sol";
import "./vaultion_managers/IVaultionManager.sol";
import "hardhat/console.sol";

contract MarginAccount {
    mapping(string => address) public brokers;
    string[] public brokersArr;
    IRoleManager public roleManager;
    IContractsStorage public contractsStorage;
    address public adminToken;
    uint256 public keyOwner;

    constructor(
        IRoleManager _roleManager,
        IContractsStorage _contractsStorage,
        address _adminToken,
        uint256 _keyOwner
    ) {
        roleManager = _roleManager;
        contractsStorage = _contractsStorage;
        adminToken = _adminToken;
        keyOwner = _keyOwner; 
    }

    function getBrokersArr() external view returns (string[] memory) {
        return brokersArr;
    }

    function provideERC20(uint256 amount, string memory name) public {
        require(msg.sender == IERC721(adminToken).ownerOf(keyOwner), "MarginAccount: you are not the owner");
        address tokenAddress = IVaultionManager(contractsStorage.vaultionManagers(name)).token();
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), amount);
    }

    function provideERC721(uint256 id, string memory name) public {
        require(msg.sender == IERC721(adminToken).ownerOf(keyOwner), "MarginAccount: you are not the owner");
        address tokenAddress = IVaultionManager(contractsStorage.vaultionManagers(name)).token();
        IERC721(tokenAddress).transferFrom(msg.sender, address(this), id);
    }

    function withdrawERC20(uint256 amount, string memory name) public {
        require(msg.sender == IERC721(adminToken).ownerOf(keyOwner), "MarginAccount: you are not the owner");
        IVaultionManager vaultionManager = IVaultionManager(contractsStorage.vaultionManagers(name)); 
        uint256 value = vaultionManager.valueByAmount(amount);
        address tokenAddress = vaultionManager.token();
        require(!contractsStorage.lendAndBorrowManager().validateSafeRatioById(keyOwner, value));
        IERC20(tokenAddress).transfer(msg.sender, amount);
    }

    function withdrawERC721(uint256 id, string memory name) public {
        require(msg.sender == IERC721(adminToken).ownerOf(keyOwner), "MarginAccount: you are not the owner");
        IVaultionManager vaultionManager = IVaultionManager(contractsStorage.vaultionManagers(name)); 
        uint256 value = vaultionManager.valueByID(id);
        address tokenAddress = vaultionManager.token();
        require(contractsStorage.lendAndBorrowManager().validateSafeRatioById(keyOwner, value));
        IERC721(tokenAddress).transferFrom(address(this), msg.sender, id);
    }

    function addBroker(string memory name, address brokerAddress) public {
        roleManager.checkBROKER_CREATOR_ROLE(msg.sender);
        brokers[name] = brokerAddress;
        brokersArr.push(name);
    }


}

