// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "./interfaces/IAdminToken.sol";
import "./interfaces/ITradeToken.sol";
import "./interfaces/IRoleManager.sol";
import "./interfaces/IContractsStorage.sol";
import "./MarginAccount.sol";
import "hardhat/console.sol";

contract MarginAccountManager {
    IAdminToken public adminToken;
    ITradeToken public tradeToken;
    IRoleManager public roleManager;
    IContractsStorage public contractsStorage;
    uint256 public tokenID = 0;

    constructor(
        IAdminToken _adminToken,
        ITradeToken _tradeToken,
        IRoleManager _roleManager,
        IContractsStorage _contractsStorage
    ) {
        adminToken = _adminToken;
        tradeToken = _tradeToken;
        roleManager = _roleManager;
        contractsStorage = _contractsStorage;
    }

    function createMarginAccount() public {
        uint256 id = tokenID;
        tokenID += 1;
        MarginAccount newMarginAccount = new MarginAccount(
            roleManager,
            contractsStorage,
            address(adminToken),
            id
        );
        adminToken.mint(msg.sender, id);
        tradeToken.mint(msg.sender, id);
        contractsStorage.setMarginAccount(id, address(newMarginAccount));
    }
}

