contracts/Pool.sol.content:
// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/WadRayMath.sol";
import "./interfaces/IRoleManager.sol";
import "hardhat/console.sol";

contract Pool is Ownable, ERC20("LP DDL", "DDL") {
    using WadRayMath for uint256;
    uint256 public constant INITIAL_RATE = 1e18;
    uint256 public constant MAX_DEPOSIT_AMOUNT = type(uint256).max;

    uint256 public excessUtilizationRate = 0.2 * 1e27;
    uint256 public optimalUtilizationRate = 0.8 * 1e27;
    uint256 public baseBorrowRate;
    uint256 public rateSlope1;
    uint256 public rateSlope2;
    bool public openDeDeLend = true;

    IERC20 public token;
    uint256 public totalLocked;
    uint256 public totalActivelyUsed;
    IRoleManager public roleManager;

    address public lendAndBorrowManager;
    bool public isItPossibleToMakeAChange = true;

    event Provide(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);

    constructor(
        IRoleManager _roleManager,
        IERC20 _token,
        uint256 _baseBorrowRate,
        uint256 _rateSlope1,
        uint256 _rateSlope2
    ) {
        roleManager = _roleManager;
        token = _token;
        baseBorrowRate = _baseBorrowRate;
        rateSlope1 = _rateSlope1;
        rateSlope2 = _rateSlope2;
    }

    modifier onlyLendAndBorrowManager() {
        require(msg.sender == lendAndBorrowManager, "Access denied");
        _;
    }

    function setExcessUtilizationRate(uint256 value) external onlyOwner {
        excessUtilizationRate = value;
    }

    function setOptimalUtilizationRate(uint256 value) external onlyOwner {
        optimalUtilizationRate = value;
    }

    function setBaseBorrowRate(uint256 value) external onlyOwner {
        baseBorrowRate = value;
    }

    function setRateSlope1(uint256 value) external onlyOwner {
        rateSlope1 = value;
    }

    function setRateSlope2(uint256 value) external onlyOwner {
        rateSlope2 = value;
    }

    function changeOpenDeDeLend(bool value) external onlyOwner {
        openDeDeLend = value;
    }

    function setLendAndBorrowManager(address value) public {
        require(isItPossibleToMakeAChange);
        roleManager.checkCONTRACT_MANAGER(msg.sender);
        lendAndBorrowManager = value;
        isItPossibleToMakeAChange = false;
    }

    function getTotalBalance() public view returns (uint256 balance) {
        balance = token.balanceOf(address(this));
    }

    function getUserBalance(address account) external view returns (uint256 share) {
        if (totalSupply() > 0)
            share = (getTotalBalance() * balanceOf(account)) / totalSupply();
        else share = 0;
    }

    function provide(uint256 amount, uint256 minShare) external returns (uint256 share) {
        require(openDeDeLend, "pauseDeDeLend");
        uint256 totalBalance = getTotalBalance() + totalLocked;
        share = totalSupply() > 0 && totalBalance > 0
            ? (amount * totalSupply()) / totalBalance
            : amount * INITIAL_RATE;
        uint256 limit = MAX_DEPOSIT_AMOUNT - totalBalance;
        require(share >= minShare, "Pool Error: The mint limit is too large");
        require(share > 0, "Pool Error: The amount is too small");
        require(
            amount <= limit,
            "Pool Error: providing USDC is paused"
        );

        token.transferFrom(msg.sender, address(this), amount);
        _mint(msg.sender, share);
        emit Provide(msg.sender, amount);
    }

    function withdraw(uint256 amount) public {
        require(amount <= getTotalBalance() - totalLocked, "Amount is too large");
        uint256 totalBalance = getTotalBalance() - totalLocked;
        uint256 burn = (amount * totalSupply()) / totalBalance;
        require(burn <= balanceOf(msg.sender), "Amount is too large");
        _burn(msg.sender, burn);
        token.transfer(msg.sender, amount);
        emit Withdraw(msg.sender, amount);
    }

    function send(address to, uint256 amount) public onlyLendAndBorrowManager {
        require(amount <= getTotalBalance());
        token.transfer(to, amount);
    }

    function addTotalLocked(uint256 value) public onlyLendAndBorrowManager {
        totalLocked += value;
    }

    function addTotalActivelyUsed(uint256 value) public onlyLendAndBorrowManager {
        totalActivelyUsed += value;
    }

    function subTotalLocked(uint256 value) public onlyLendAndBorrowManager {
        totalLocked -= value;
    }

    function subTotalActivelyUsed(uint256 value) public onlyLendAndBorrowManager {
        totalActivelyUsed -= value;
    }

    function calculateInterestRate()
        external
        view
        returns (
            uint256 currentBorrowRate
        )
    {
        uint256 _availableLiquidity = getTotalBalance() - totalLocked;
        uint256 utilizationRate = (totalLocked == 0 && _availableLiquidity == 0)
            ? 0
            : (totalLocked+totalActivelyUsed).rayDiv(_availableLiquidity+totalLocked+totalActivelyUsed);
        if (utilizationRate > optimalUtilizationRate) {
            uint256 excessUtilizationRateRatio = (utilizationRate-optimalUtilizationRate).rayDiv(excessUtilizationRate);

            currentBorrowRate = baseBorrowRate+(rateSlope1)+(
                rateSlope2.rayMul(excessUtilizationRateRatio)
            );
        } else if (utilizationRate == 0) {
            currentBorrowRate = baseBorrowRate;
        } else {
            currentBorrowRate = baseBorrowRate+(
                utilizationRate.rayDiv(optimalUtilizationRate).rayMul(rateSlope1)
            );
        }
    }
}

