// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "./interfaces/IContractsStorage.sol";
import "./interfaces/IMarginAccount.sol";
import "./interfaces/IRiskManager.sol";
import "./vaultion_managers/IVaultionManager.sol";
import "./brokers/IBroker.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "hardhat/console.sol";

contract RiskManager {
    IERC721 public tradeToken;
    IERC721 public adminToken;
    IContractsStorage public contractsStorage;
    address public lendAndBorrowManager;
    IERC20 public usdc;

    struct SuccessReport {
        address broker;
        bool success;
    }

    constructor(
        IERC721 _adminToken,
        IERC721 _tradeToken,
        IContractsStorage _contractsStorage,
        address _lendAndBorrowManager,
        IERC20 _usdc
    ) {
        adminToken = _adminToken;
        tradeToken = _tradeToken;
        contractsStorage = _contractsStorage;
        lendAndBorrowManager = _lendAndBorrowManager;
        usdc = _usdc;
    }

    function positionsValue(uint256 id) public view returns (uint256 totalCost) {
        address accountAddress = contractsStorage.marginAccounts(id);
        IMarginAccount marginAccount = IMarginAccount(accountAddress);
        string[] memory arrProducts = marginAccount.getBrokersArr();
        for (uint256 i; i<arrProducts.length; i++) {
            string memory product = arrProducts[i];
            address brokerAddress = marginAccount.brokers(product);
            totalCost += IVaultionManager(contractsStorage.vaultionManagers(product)).valueByAddress(brokerAddress);
            totalCost += usdc.balanceOf(brokerAddress);
        }
    }

    function valueByAddress(uint256 id) public view returns (uint256 totalCost) {
        address accountAddress = contractsStorage.marginAccounts(id);
        string[] memory arrProducts = contractsStorage.getProductArr();
        for (uint256 i; i<arrProducts.length; i++) {
            string memory product = arrProducts[i];
            totalCost += IVaultionManager(contractsStorage.vaultionManagers(product)).valueByAddress(accountAddress);
        }
        totalCost += usdc.balanceOf(accountAddress);
    }

    function liquidateBroker(address broker) external {
        require(msg.sender == lendAndBorrowManager);
        IBroker(broker).liquidatePositions();
    }

    function liquidateAllBrokers(uint256 id) external returns (SuccessReport[] memory) {
        require(msg.sender == lendAndBorrowManager);
        address accountAddress = contractsStorage.marginAccounts(id);
        IMarginAccount marginAccount = IMarginAccount(accountAddress);
        string[] memory arrBrokers = marginAccount.getBrokersArr();
        SuccessReport[] memory report =  new SuccessReport[](arrBrokers.length);
        for (uint256 i; i<arrBrokers.length; i++) {
            address brokerAddress = marginAccount.brokers(arrBrokers[i]);
            try IBroker(brokerAddress).liquidatePositions() {
                report[i] = SuccessReport(brokerAddress, true);
            } catch {
                report[i] = SuccessReport(brokerAddress, false);
            }

        }
        return report;
    }

    function withdrawFromAllBrokers(uint256 id) external returns (SuccessReport[] memory) {
        require(msg.sender == lendAndBorrowManager);
        address accountAddress = contractsStorage.marginAccounts(id);
        IMarginAccount marginAccount = IMarginAccount(accountAddress);
        string[] memory arrBrokers = marginAccount.getBrokersArr();
        SuccessReport[] memory report =  new SuccessReport[](arrBrokers.length);
        for (uint256 i; i<arrBrokers.length; i++) {
            address brokerAddress = marginAccount.brokers(arrBrokers[i]);
            try IBroker(brokerAddress).withdrawERC20(usdc.balanceOf(brokerAddress), "USDC") {
                report[i] = SuccessReport(brokerAddress, true);
            } catch {
                report[i] = SuccessReport(brokerAddress, false);
            }

        }
        return report;

    }

    function withdrawAllFromMarginAccount(uint256 id) external {
        require(msg.sender == lendAndBorrowManager);
        address accountAddress = contractsStorage.marginAccounts(id);
        IMarginAccount(accountAddress).withdrawERC20(usdc.balanceOf(accountAddress), "USDC");
    }

    function withdrawERC20FromMarginAccount(uint256 id, string memory name, uint256 amount) external {
        require(msg.sender == lendAndBorrowManager);
        address accountAddress = contractsStorage.marginAccounts(id);
        IMarginAccount(accountAddress).withdrawERC20(amount, name);
    }
}

