// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract RoleManager is Ownable, AccessControl {
    bytes32 public constant MARGIN_ACCOUNT_MANAGER_ROLE = keccak256("MARGIN_ACCOUNT_MANAGER_ROLE");
    bytes32 public constant BROKER_CREATOR_ROLE = keccak256("BROKER_CREATOR_ROLE");
    bytes32 public constant CONTRACT_MANAGER = keccak256("CONTRACT_MANAGER");
    bool public add_cotracts_role_state = true; 

    function setBrokerCreatorRole(address value, bool state) external onlyOwner {
        if (state) {
            _setupRole(BROKER_CREATOR_ROLE, value);
        } else {
            _revokeRole(BROKER_CREATOR_ROLE, value);
        }
    }

    function setAdminRole(address value, bool state) external onlyOwner {
        require(add_cotracts_role_state);
        if (state) {
            _setupRole(CONTRACT_MANAGER, value);
        } else {
            _revokeRole(CONTRACT_MANAGER, value);
        }
    }

    function setMarginAccountManagerRole(address value, bool state) external onlyOwner {
        if (state) {
            _setupRole(MARGIN_ACCOUNT_MANAGER_ROLE, value);
        } else {
            _revokeRole(MARGIN_ACCOUNT_MANAGER_ROLE, value);
        }
    }

    function freezeFunction() external onlyOwner {
        add_cotracts_role_state = false;
    }

    function checkBROKER_CREATOR_ROLE(address account) external view {
        require(hasRole(BROKER_CREATOR_ROLE, account));
    }

    function checkCONTRACT_MANAGER(address account) external view {
        require(hasRole(CONTRACT_MANAGER, account));
    }

    function checkMARGIN_ACCOUNT_MANAGER_ROLE(address account) external view {
        require(hasRole(MARGIN_ACCOUNT_MANAGER_ROLE, account));
    }
}

