// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.6;

import "./IVaultionManager.sol";
import "../interfaces/IVault.sol";
import "../interfaces/ITimelock.sol";
import "hardhat/console.sol";

contract VaultionManager_GMX_SHORT {
    IVault public vault;
    address[] public arrTradingPair;
    address public token;

    constructor(
        address _token,
        IVault _vault,
        address[] memory _arrTradingPair
    ) {
        token = _token;
        vault = _vault;
        arrTradingPair = _arrTradingPair;
    }

    function valueByAddress(address path) external view returns (uint256) {
        uint256 totalValue;
        for (uint256 i; i < arrTradingPair.length; i++) {
            totalValue += gmxRuturnValue(path, arrTradingPair[i]);
        }
        return totalValue;
    }

    function gmxRuturnValue(address path, address indexToken) internal view returns(uint256 amountReturn) {
        address[] memory gmxPath = new address[](1);
        gmxPath[0] = token;
        (uint256 size, uint256 collateral, , , , , , ) = getPosition(path, indexToken);
        if (size != 0) {
            (bool isProfit, uint256 delta) = getPositionDelta(path, indexToken);
            uint256 closeValue = (isProfit ? collateral : delta);
            amountReturn = decreasePositionReturnValue(
                path,
                gmxPath[0],
                indexToken,
                closeValue,
                size,
                false
            );

        }
    }

    function getPosition(address _account, address _indexToken) 
        public 
        view 
        returns (
            uint256 size,
            uint256 collateral,
            uint256 averagePrice,
            uint256 entryFundingRate,
            uint256 reserveAmount,
            uint256 realisedPnl,
            bool isProfit,
            uint256 lastIncreasedTime
    ) {
        (
            size,
            collateral,
            averagePrice,
            entryFundingRate,
            reserveAmount,
            realisedPnl,
            isProfit,
            lastIncreasedTime
        ) = vault.getPosition(_account, token, _indexToken, false);
    }

    function getPositionDelta(address _account, address _indexToken)
        public
        view
        returns (bool isProfit, uint256 profit) {
            return vault.getPositionDelta(_account, token, _indexToken, false);
        }

    function decreasePositionReturnValue(
        address _account,
        address _collateralToken,
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        bool _isLong
    ) internal view returns (uint256) {
        (uint256 usdOut, uint256 usdOutAfterFee) = _reduceCollateral(
            _account,
            _collateralToken,
            _indexToken,
            _collateralDelta,
            _sizeDelta,
            _isLong
        );
        if (usdOut > 0) {
            uint256 amountOutAfterFees = vault.usdToTokenMin(
                _collateralToken,
                usdOutAfterFee
            );
            return amountOutAfterFees;
        }
        return 0;
    }

    function _reduceCollateral(
        address _account,
        address _collateralToken,
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        bool _isLong
    ) private view returns (uint256, uint256) {
        (
            uint256 size,
            uint256 collateral,
            uint256 averagePrice,
            uint256 entryFundingRate,
            ,
            ,
            ,
            uint256 lastIncreasedTime
        ) = vault.getPosition(_account, _collateralToken, _indexToken, _isLong);
        uint256 fee = _collectMarginFees(
            _collateralToken,
            _sizeDelta,
            size,
            entryFundingRate
        );
        (bool hasProfit, uint256 delta) = vault.getDelta(
            _indexToken,
            size,
            averagePrice,
            _isLong,
            lastIncreasedTime
        );
        uint256 adjustedDelta = (_sizeDelta * delta) / size;
        uint256 usdOut;
        if (hasProfit && adjustedDelta > 0) {
            usdOut = adjustedDelta;
        }
        if (!hasProfit && adjustedDelta > 0) {
            collateral -= adjustedDelta;
        }
        if (_collateralDelta > 0) {
            usdOut += _collateralDelta;
            collateral -= _collateralDelta;
        }
        if (size == _sizeDelta) {
            usdOut = usdOut + collateral;
        }
        uint256 usdOutAfterFee = usdOut;
        if (usdOut > fee) {
            usdOutAfterFee = usdOut - fee;
        }
        return (usdOut, usdOutAfterFee);
    }

    function _collectMarginFees(
        address _collateralToken,
        uint256 _sizeDelta,
        uint256 _size,
        uint256 _entryFundingRate
    ) private view returns (uint256) {
        uint256 feeUsd = _getPositionFee(_sizeDelta);
        uint256 fundingFee = vault.getFundingFee(
            _collateralToken,
            _size,
            _entryFundingRate
        );
        feeUsd += fundingFee;
        return feeUsd;
    }

    function _getPositionFee(uint256 _sizeDelta) private view returns (uint256) {
        if (_sizeDelta == 0) {
            return 0;
        }
        uint256 afterFeeUsd = (_sizeDelta *
            (vault.BASIS_POINTS_DIVISOR() -
                ITimelock(vault.gov()).marginFeeBasisPoints())) /
            vault.BASIS_POINTS_DIVISOR();
        return _sizeDelta - afterFeeUsd;
    }

    function _collectSwapFees(
        uint256 _amount,
        uint256 _feeBasisPoints
    ) private view returns (uint256) {
        uint256 afterFeeAmount = (_amount *
            (vault.BASIS_POINTS_DIVISOR() - _feeBasisPoints)) /
            vault.BASIS_POINTS_DIVISOR();
        return afterFeeAmount;
    }
}

