pragma solidity 0.8.6;

import "./IVaultionManager.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract VaultionManager_USDC {
    address public token;
    AggregatorV3Interface priceFeed;

    constructor(
        address _token,
        AggregatorV3Interface _priceFeed
    ) {
        token = _token;
        priceFeed = _priceFeed;
    }

    function valueByAddress(address path) public view returns (uint256) {
        return valueByAmount(IERC20(token).balanceOf(path));
    }

    function valueByAmount(uint256 amount) public view returns (uint256) {
        return amount*getLatestPrice()/getDecimals();
    }

    function valueByID(uint256 id) public view returns (uint256){}

    function getDecimals() public view returns (uint256) {
        return 10**priceFeed.decimals();
    }

    function getLatestPrice() public view returns (uint256) {
        (,int price,,,) = priceFeed.latestRoundData();
        return uint256(price);
    }
}

